  } catch(e) {}
  let initData = {};
  try { initData = $node['Init & ACL Pre-Filter V3.4'].json; } catch(e) {}
  if (llmContent) {
    return [{ json: { response: llmContent, trace_id: initData.trace_id || 'tr-'+Date.now(), sources: sources.slice(0,5), model: 'meta-llama/llama-3.3-70b-instruct:free', embedding_fallback: sources.length===0, engine: 'STANDARD', timestamp: new Date().toISOString() }}];
  }
  return [{ json: { response: 'Unable to generate answer', trace_id: initData.trace_id || 'tr-'+Date.now(), error: true, engine: 'STANDARD', timestamp: new Date().toISOString() }}];
} catch(e) {
  return [{ json: { response: 'Error: '+e.message, error: true, engine: 'STANDARD', timestamp: new Date().toISOString() }}];
}

// Query Merger V3.4 - Prepare queries for retrieval
const initData = $node['Init & ACL Pre-Filter V3.4'].json;
const needsDecomposition = initData.needs_decomposition;

let queriesToProcess = [];

if (needsDecomposition) {
  try {
    const decomposed = JSON.parse($node['Query Decomposer (V.3.4)'].json?.choices?.[0]?.message?.content || '{}');

    if (decomposed.is_simple || !decomposed.sub_queries?.length) {
      queriesToProcess = [initData.query];
    } else {
      // Add original + sub-queries for comprehensive retrieval
      queriesToProcess = [initData.query, ...decomposed.sub_queries.slice(0, 3)];
    }
  } catch (e) {
    queriesToProcess = [initData.query];
  }
} else {
  queriesToProcess = [initData.query];
}

return [{
  json: {
    queries: queriesToProcess,
    primary_query: initData.query,
    is_decomposed: queriesToProcess.length > 1,
    topK_per_query: Math.ceil(initData.topK / queriesToProcess.length),
    trace_id: initData.trace_id,
    user_context: initData.user_context,
    acl_filter: initData.acl_filter
  }
}];

