    latency_ms: errorData.latency_ms || 0,
    http_status: errorData.http_status || null
  }
};

return [{ json: logEntry }];

// Execution Summary Logger for standard
const input = $input.first().json;
const timestamp = new Date().toISOString();

const summary = {
  timestamp: timestamp,
  pipeline: 'standard',
  question_id: input.trace_id || input.question_id || 'q-' + Date.now(),
  query: (input.query || input.original_query || '').slice(0, 300),
  success: !!(input.response || input.final_response || input.answer),
  response_length: (input.response || input.final_response || input.answer || '').length,
  confidence: input.confidence || 0,
  latency_ms: input.latency_ms || 0,
  engine: input.engine || input.selected_engine || 'standard',
  n8n_context: {
    workflow_id: $workflow.id,
    execution_id: $execution.id
  }
};

return [{ json: summary }];

// Init & ACL Pre-Filter V3.4 - With Adaptive TopK
const rawInput = $input.first().json;
const input = rawInput.body || rawInput;

// Parse query robustement
let queryStr = '';
if (typeof input.question === 'string') {
  queryStr = input.question;
} else if (input.question && typeof input.question === 'object') {
  queryStr = JSON.stringify(input.question);
} else {
  queryStr = String(input.question || '');
}

const userContext = input.user_context || {};
const tenantId = userContext.tenant_id || input.tenant_id || 'default';
const userGroups = Array.isArray(userContext.groups) ? userContext.groups : ['admin', 'guest'];

// V3.4: Analyse de complexité pour adaptive top_k
const COMPLEXITY_INDICATORS = {
  simple: /^(qu'est-ce que|définition|c'est quoi)/i,
  moderate: /(comment|pourquoi|expliquer)/i,
  complex: /(comparer|analyser|impact|relation|entre.*et)/i,
  multi_hop: /(quel.*puis|d'abord.*ensuite|en tenant compte)/i
};

let complexity = 'moderate';
let adaptiveTopK = 15;

if (COMPLEXITY_INDICATORS.simple.test(queryStr)) {
  complexity = 'simple';
  adaptiveTopK = 8;
} else if (COMPLEXITY_INDICATORS.multi_hop.test(queryStr)) {
  complexity = 'complex';
  adaptiveTopK = 25;
} else if (COMPLEXITY_INDICATORS.complex.test(queryStr)) {
  complexity = 'complex';
  adaptiveTopK = 20;
}

// V3.4: Detect if query needs decomposition
const needsDecomposition = queryStr.length > 100 || 
                          queryStr.includes(' et ') ||
                          queryStr.includes(' ainsi que ') ||
                          COMPLEXITY_INDICATORS.complex.test(queryStr);

// ACL filter (désactivé par défaut pour compatibilité)
const disableAcl = (input.disable_acl === true);
const aclFilter = disableAcl ? {} : {
  "$or": [
    { "tenant_id": tenantId, "allowed_groups": { "$in": userGroups } },
    { "tenant_id": { "$exists": false } }
  ]
};

return [{
  json: {
    trace_id: input.trace_id || 'tr-' + Date.now(),
    query: queryStr.trim(),
    acl_filter: aclFilter,
    acl_disabled: disableAcl,
    user_context: { tenant_id: tenantId, groups: userGroups },

