// ============================================
// PATCH 07 - RRF Merge & Rank V3.4.2
// FIX: Lecture robuste des 3 sources (HyDE, Original, BM25)
// ============================================
//
// PROBLÈME RÉSOLU:
// Le code précédent échouait silencieusement sur BM25
// car le nœud Merge ne passait pas les données correctement.
//
// SOLUTION:
// Lire directement depuis chaque nœud source au lieu
// de dépendre du Merge node.
//
// INSTRUCTIONS:
// 1. Ouvrir le nœud "RRF Merge & Rank V3.4" dans le workflow RAG Standard
// 2. Remplacer tout le code par celui-ci
// 3. Sauvegarder
// ============================================

// RRF Merge & Rank V3.4.2 - Direct source reading
const k = 60; // Paramètre RRF standard
const BOOSTS = { hyde: 1.5, bm25: 1.2, pinecone: 1.0 };
let scores = {};
let warnings = [];
let sourcesAvailable = 0;

// Récupérer les métadonnées
const initData = $node['Init & ACL Pre-Filter V3.4'].json;
const traceId = initData.trace_id;

// V3.4: Check if queries were decomposed
let isDecomposed = false;
try {
  const queryData = $node['Query Merger V3.4']?.json;
  isDecomposed = queryData?.is_decomposed || false;
} catch (e) {
  // Query Merger pas dans le flux
}
const decompositionBoost = isDecomposed ? 1.1 : 1.0;

// === HELPER FUNCTIONS ===
function extractContent(metadata) {
  return metadata?.text || metadata?.content || metadata?.chunk_text || metadata?.page_content || '';
}

function safeReadNode(nodeName) {
  try {
    return $node[nodeName]?.json;
  } catch (e) {
    console.log(`[${traceId}] Cannot read ${nodeName}: ${e.message}`);
    return null;
  }
}

// === SOURCE 1: HyDE Pinecone ===
console.log(`[${traceId}] Reading HyDE results...`);
try {
  const hydeData = safeReadNode('HTTP Pinecone Query HyDE');
  const hydeMatches = hydeData?.matches || [];

  if (hydeMatches.length > 0) {
    sourcesAvailable++;
    hydeMatches.forEach((item, index) => {
      const id = item.id;
      const rrfScore = (1 / (k + index + 1)) * BOOSTS.hyde * decompositionBoost;

      if (scores[id]) { 
        scores[id].score += rrfScore; 
        if (!scores[id].sources.includes('hyde')) {
          scores[id].sources.push('hyde');
        }
      } else { 
        scores[id] = { 
          score: rrfScore, 
          data: item.metadata, 
          pineconeScore: item.score, 
          sources: ['hyde'] 
        }; 
      }
    });
    console.log(`[${traceId}] HyDE: ${hydeMatches.length} results added`);
  } else { 
    warnings.push('HyDE: 0 results'); 
  }
} catch(e) { 
  warnings.push('HyDE error: ' + e.message);
  console.error(`[${traceId}] HyDE error:`, e.message);
}

// === SOURCE 2: Original Pinecone ===
console.log(`[${traceId}] Reading Original Embedding results...`);
try {
  const originalData = safeReadNode('HTTP Pinecone Query Original');
  const originalMatches = originalData?.matches || [];

  if (originalMatches.length > 0) {
    sourcesAvailable++;
    originalMatches.forEach((item, index) => {
      const id = item.id;
      const rrfScore = (1 / (k + index + 1)) * BOOSTS.pinecone;

      if (scores[id]) { 
        scores[id].score += rrfScore; 
        if (!scores[id].sources.includes('original')) {
          scores[id].sources.push('original'); 
        }
      } else { 
        scores[id] = { 
          score: rrfScore, 
          data: item.metadata, 
          pineconeScore: item.score, 
          sources: ['original'] 
        }; 
      }
    });
    console.log(`[${traceId}] Original: ${originalMatches.length} results added`);
  } else { 
    warnings.push('Original: 0 results'); 
  }
} catch(e) { 
  warnings.push('Original error: ' + e.message);
  console.error(`[${traceId}] Original error:`, e.message);
}

// === SOURCE 3: BM25 Postgres ===
console.log(`[${traceId}] Reading BM25 results...`);
try {
  const bm25Raw = safeReadNode('BM25 Search Postgres');

  // BM25 peut retourner différents formats:
  // - Array de résultats
  // - Objet unique avec {id, content, bm25_score}
  // - Null/undefined si erreur

  let bm25Data = [];

  if (Array.isArray(bm25Raw)) {
    bm25Data = bm25Raw;
  } else if (bm25Raw && typeof bm25Raw === 'object') {
    if (bm25Raw.id) {
      // Single result object
      bm25Data = [bm25Raw];
    } else if (bm25Raw.error) {
      // Postgres error
      throw new Error(bm25Raw.error.message || 'Postgres error');
    }
  }

  // Filtrer les résultats valides
  bm25Data = bm25Data.filter(item => item && item.id && item.content);

  if (bm25Data.length > 0) {
    sourcesAvailable++;
    bm25Data.forEach((item, index) => {
      const id = 'bm25-' + String(item.id);
      const rrfScore = (1 / (k + index + 1)) * BOOSTS.bm25;

      if (scores[id]) { 
        scores[id].score += rrfScore; 
        scores[id].sources.push('bm25'); 
      } else { 
        scores[id] = { 
          score: rrfScore, 
          data: { 
            content: item.content, 
            source: item.source || 'postgres', 
            text: item.content 
          }, 
          bm25Score: item.bm25_score, 
          sources: ['bm25'] 
        }; 
      }
    });
    console.log(`[${traceId}] BM25: ${bm25Data.length} results added`);
  } else { 
    warnings.push('BM25: 0 results (may need tenant_id configuration)'); 
  }
} catch(e) { 
  warnings.push('BM25 error: ' + e.message);
  console.error(`[${traceId}] BM25 error:`, e.message);
}

// === RANKING FINAL ===
// R03 SOTA 2026: Min-max normalization of RRF scores before final ranking
const allEntries = Object.entries(scores)
  .map(([id, r]) => ({ id, ...r }));

if (allEntries.length > 1) {
  const rawScores = allEntries.map(e => e.score);
  const minScore = Math.min(...rawScores);
  const maxScore = Math.max(...rawScores);
  const range = maxScore - minScore;
  if (range > 0) {
    allEntries.forEach(e => {
      e.raw_rrf_score = e.score;
      e.score = (e.score - minScore) / range;
    });
    console.log(`[${traceId}] RRF normalization applied: min=${minScore.toFixed(6)}, max=${maxScore.toFixed(6)}`);
  }
}

const ranked = allEntries.sort((a, b) => b.score - a.score);

const hasResults = ranked.length > 0;

console.log(`[${traceId}] RRF complete: ${ranked.length} unique docs from ${sourcesAvailable} sources`);
if (warnings.length > 0) {
  console.log(`[${traceId}] Warnings: ${warnings.join(', ')}`);
}

// === OUTPUT ===
return [{
  json: {
    results: ranked.slice(0, 15).map(r => ({
      id: r.id,
      content: extractContent(r.data),
      source: r.data?.source || r.data?.dataset || 'unknown',
      rrf_score: Math.round(r.score * 10000) / 10000,
      raw_rrf_score: r.raw_rrf_score ? Math.round(r.raw_rrf_score * 10000) / 10000 : undefined,
      pinecone_score: r.pineconeScore,
      bm25_score: r.bm25Score,
      sources_used: r.sources
    })),
    metadata: { 
      sources_available: sourcesAvailable,
      total_unique_docs: ranked.length,
      warnings: warnings,
      is_decomposed: isDecomposed,
      rrf_normalized: true,
      version: '3.4.3',
      trace_id: traceId
    },
    skip_reranker: !hasResults
  }
}];

