// ============================================
// PATCH 02-FIX - Rerank Merger V3.4.2
// FIX: Gestion correcte du type d'erreur Cohere
// ============================================
// 
// PROBLÈME CORRIGÉ:
// L'erreur "cohereError.includes is not a function" se produit
// car cohereError est un OBJET (avec .message), pas une string.
//
// INSTRUCTIONS:
// 1. Ouvrir le nœud "Rerank Merger" dans le workflow RAG Standard
// 2. Remplacer tout le code par celui-ci
// 3. Sauvegarder
// ============================================

const rrfResults = $node['RRF Merge & Rank V3.4'].json.results || [];
const initData = $node['Init & ACL Pre-Filter V3.4'].json;
const metadata = $node['RRF Merge & Rank V3.4'].json.metadata || {};
const skipReranker = $node['RRF Merge & Rank V3.4'].json.skip_reranker;
const traceId = initData.trace_id;

// === CAS 1: Aucun résultat RRF ===
if (rrfResults.length === 0 || skipReranker) {
  console.log(`[${traceId}] No RRF results or skip_reranker - returning empty`);
  return [{
    json: {
      results: [],
      metadata: { ...metadata, reranked: false, empty_database: true },
      skip_llm: false,
      fallback_response: {
        response: "Je n'ai trouvé aucun document pertinent dans la base de connaissances.",
        sources: [],
        confidence: 0,
        trace_id: traceId,
        warning: "NO_DOCUMENTS_FOUND"
      }
    }
  }];
}

// === CAS 2: Analyser la réponse Cohere ===
const cohereResponse = $json;
const cohereResults = cohereResponse.results || [];

// L'erreur peut être un objet ou une string - extraire le message
const cohereErrorObj = cohereResponse.error;
let cohereErrorMsg = '';
if (cohereErrorObj) {
  if (typeof cohereErrorObj === 'string') {
    cohereErrorMsg = cohereErrorObj;
  } else if (typeof cohereErrorObj === 'object') {
    cohereErrorMsg = cohereErrorObj.message || JSON.stringify(cohereErrorObj);
  }
}

const httpStatus = cohereResponse.statusCode || cohereResponse.status || cohereErrorObj?.status;

// Détecter rate limit (429) ou autres erreurs
const isRateLimited = httpStatus === 429 || 
                      cohereErrorMsg.includes('429') ||
                      cohereErrorMsg.toLowerCase().includes('rate');
const hasError = cohereErrorObj || cohereResults.length === 0;

if (hasError) {
  // === FALLBACK: Utiliser les scores RRF sans reranking ===
  const reason = isRateLimited ? 'COHERE_RATE_LIMITED' : 'COHERE_ERROR';
  console.log(`[${traceId}] Cohere ${reason}: ${cohereErrorMsg || 'No results'}`);

  // Trier par score RRF décroissant
  const sortedByRRF = [...rrfResults].sort((a, b) => (b.rrf_score || 0) - (a.rrf_score || 0));

  return [{
    json: {
      results: sortedByRRF.slice(0, 10).map(r => ({
        ...r,
        combined_score: r.rrf_score, // Utiliser RRF comme score final
        rerank_score: null
      })),
      metadata: { 
        ...metadata, 
        reranked: false, 
        cohere_skipped: true,
        skip_reason: reason,
        fallback_method: 'RRF_ONLY',
        error_details: cohereErrorMsg
      },
      skip_llm: false
    }
  }];
}

// === CAS 3: Cohere OK - Fusionner les scores ===
console.log(`[${traceId}] Cohere OK: ${cohereResults.length} reranked results`);

const reranked = cohereResults.map(cr => {
  const original = rrfResults[cr.index];
  if (!original) {
    console.log(`[${traceId}] Warning: Cohere index ${cr.index} not found in RRF results`);
    return null;
  }

  // Score combiné: 40% RRF + 60% Cohere
  const combinedScore = (original.rrf_score || 0) * 0.4 + cr.relevance_score * 0.6;

  return { 
    ...original, 
    rerank_score: cr.relevance_score,
    combined_score: combinedScore
  };
}).filter(r => r !== null);

// Trier par score combiné
reranked.sort((a, b) => b.combined_score - a.combined_score);

return [{ 
  json: { 
    results: reranked.slice(0, 10), 
    metadata: { 
      ...metadata, 
      reranked: true,
      cohere_results_count: cohereResults.length
    }, 
    skip_llm: false 
  } 
}];

