// CORRECTED Neo4j Query Builder - Neo4j 5.x syntax compatible
// Fixed: EXISTS() -> IS NOT NULL

const initData = $node['OTEL Init'].json;
let hydeResponse = {};
try {
  hydeResponse = JSON.parse($json.choices?.[0]?.message?.content || '{}');
} catch (e) {
  hydeResponse = { entities: [], hyde_document: '' };
}

const hydeEntities = hydeResponse.entities || [];

// Strict validation of entity names
const validEntities = hydeEntities
  .filter(e => e && typeof e.name === 'string')
  .map(e => e.name.replace(/[^a-zA-Z√Ä-√ø0-9\-_\s]/g, '').substring(0, 100))
  .slice(0, 20);

if (validEntities.length === 0) {
  return [{ 
    json: {
      skip_neo4j: true, 
      reason: 'No valid entities extracted', 
      hyde_document: hydeResponse.hyde_document 
    }
  }];
}

// === CONFIGURATION TRAVERSAL ===
const MAX_DEPTH = 3;
const MAX_RESULTS = 100;

// === CORRECTED RELATIONSHIP WEIGHTS (matching YOUR Neo4j) ===
const RELATIONSHIP_WEIGHTS = {
  'A_CREE': 1.5,           // Created
  'CONNECTE': 1.3,         // Connected to
  'ETUDIE': 1.2,           // Studies
  'UTILISE': 1.2,          // Uses
  'EXPOSE_A': 1.1,         // Exposes to
  'CAUSE_PAR': 1.4,        // Caused by
  'PROTEGE_CONTRE': 1.3,   // Protects against
  'VISE_A_LIMITER': 1.1,   // Aims to limit
  'SOUS_ENSEMBLE_DE': 1.0, // Subset of
  'CIBLE': 1.2,            // Targets
  'ETEND': 1.0             // Extends
};

// === NEO4J 5.x COMPATIBLE QUERY - FIXED EXISTS() ===
const correctedQuery = `
MATCH (n)
WHERE ((((n.name IN $entity_names
      OR ANY(ename IN $entity_names WHERE toLower(n.name) CONTAINS toLower(ename))
      OR ANY(ename IN $entity_names WHERE toLower(ename) CONTAINS toLower(n.name)))
      OR ANY(ename IN $entity_names WHERE toLower(n.name) CONTAINS toLower(ename))
      OR ANY(ename IN $entity_names WHERE toLower(ename) CONTAINS toLower(n.name)))
      OR ANY(ename IN $entity_names WHERE toLower(n.name) CONTAINS toLower(ename))
      OR ANY(ename IN $entity_names WHERE toLower(ename) CONTAINS toLower(n.name))) OR toLower(n.name) IN [x IN $entity_names | toLower(x)] OR ANY(ename IN $entity_names WHERE size(ename) > 3 AND toLower(n.name) CONTAINS toLower(ename)))
  AND (n.tenant_id = $tenant_id OR n.tenant_id IS NULL)
WITH n, 
     CASE 
       WHEN n:Organization THEN 1.3
       WHEN n:Person THEN 1.2
       WHEN n:City THEN 1.1
       WHEN n:Museum THEN 1.2
       WHEN n:Technology THEN 1.0
       WHEN n:Disease THEN 1.0
       ELSE 0.9 
     END as entity_priority
ORDER BY entity_priority DESC
LIMIT 10

OPTIONAL MATCH path = (n)-[r*1..${MAX_DEPTH}]-(m)
WHERE (m.tenant_id = $tenant_id OR m.tenant_id IS NULL)
WITH path, n as startNode, entity_priority,
     reduce(score = entity_priority, rel IN relationships(path) | 
       score + CASE type(rel)
         WHEN 'A_CREE' THEN 1.5
         WHEN 'CAUSE_PAR' THEN 1.4
         WHEN 'CONNECTE' THEN 1.3
         WHEN 'PROTEGE_CONTRE' THEN 1.3
         WHEN 'ETUDIE' THEN 1.2
         WHEN 'UTILISE' THEN 1.2
         WHEN 'CIBLE' THEN 1.2
         WHEN 'EXPOSE_A' THEN 1.1
         WHEN 'VISE_A_LIMITER' THEN 1.1
         WHEN 'SOUS_ENSEMBLE_DE' THEN 1.0
         WHEN 'ETEND' THEN 1.0
         ELSE 0.8
       END
     ) as path_score,
     length(path) as path_length

WHERE path_score > (path_length * 0.3)

RETURN 
  startNode.name as start_entity,
  [node IN nodes(path) | {name: node.name, type: labels(node)[0]}] as path_nodes,
  [rel IN relationships(path) | type(rel)] as path_relations,
  path_score,
  path_length
ORDER BY path_score DESC
LIMIT $max_results
`;

return [{
  json: {
    skip_neo4j: false,
    query: correctedQuery,
    parameters: {
      entity_names: validEntities,
      tenant_id: initData.user_context.tenant_id,
      max_results: MAX_RESULTS
    },
    hyde_document: hydeResponse.hyde_document,
    entities: validEntities,
    traversal_config: {
      max_depth: MAX_DEPTH,
      relationship_weights: RELATIONSHIP_WEIGHTS
    },
    _debug: {
      entities_found: validEntities.length,
      schema_corrected: true,
      french_relationships: true,
      neo4j_5x_compatible: true
    }
  }
}];

// Enhanced Merge for Deep Traversal Results - CONFIRM√â
// Handles paths up to 4 hops with intelligent deduplication

// === COLLECT ALL SOURCES ===
let graphResults = [];
let vectorResults = [];
let communityResults = [];

// Graph Results (from Deep Traversal)
try {
  const neo4jData = $node['Shield #4: Neo4j Guardian Traversal'].json;

  let rows = [];
  if (neo4jData.data?.values?.length > 0) {
    // Neo4j query/v2 API format
    rows = neo4jData.data.values;
  } else if (neo4jData.results?.[0]?.data) {
    rows = neo4jData.results[0].data.map(d => d.row);
  } else if (neo4jData.body?.data?.values?.length > 0) {
    rows = neo4jData.body.data.values;
  } else if (neo4jData.body?.results?.[0]?.data) {
    rows = neo4jData.body.results[0].data.map(d => d.row);
  } else if (Array.isArray(neo4jData.body)) {
    rows = neo4jData.body;
  } else if (Array.isArray(neo4jData)) {
    rows = neo4jData;
  }

  if (rows.length > 0) {
    graphResults = rows.map((row, idx) => {
      const pathNodes = Array.isArray(row) ? row[1] : row.path_nodes;
      const pathRelations = Array.isArray(row) ? row[2] : row.path_relations;
      const pathScore = Array.isArray(row) ? row[3] : row.path_score;
      const pathLength = Array.isArray(row) ? row[4] : row.path_length;

      return {
        source: 'GRAPH',
        id: `graph-${idx}`,
        content: formatGraphPath(pathNodes, pathRelations),
        path_nodes: pathNodes || [],
        path_relations: pathRelations || [],
        path_score: pathScore || 1.0,
        path_length: pathLength || 1,
        start_entity: Array.isArray(row) ? row[0] : row.start_entity
      };
    });
  }
} catch (e) { 
  console.log('Graph parse error:', e.message); 
}

// Vector Results (Pinecone)
try {
  const pineconeData = (() => {
  try { return $node['WF3: Pinecone HyDE Search'].json; }
  catch(e) { return { matches: [] }; }
})();
  const matches = pineconeData.body?.matches || pineconeData.matches || [];
  vectorResults = matches.map((m, idx) => ({
    source: 'VECTOR',
    id: m.id || `vector-${idx}`,
    content: m.metadata?.text || m.metadata?.content || '',
    score: m.score || 0,
    metadata: m.metadata
  }));
} catch (e) { 
  console.log('Vector parse error:', e.message); 
}

// Community Summaries
try {
  const communityData = $node['Community Summaries Fetch'].json;
  const commArray = Array.isArray(communityData) ? communityData : 
                    (communityData ? [communityData] : []);
  communityResults = commArray
    .filter(c => c && c.summary)
    .map((c, idx) => ({
      source: 'COMMUNITY',
      id: c.community_id || `comm-${idx}`,
      content: c.summary || '',
      title: c.title,
      entity_count: c.entity_count
    }));
} catch (e) { 
  console.log('Community parse error:', e.message); 
}

// === HELPER: Format graph path for reading ===
function formatGraphPath(nodes, relations) {
  if (!nodes || nodes.length === 0) return '';

  let pathStr = nodes[0]?.name || (typeof nodes[0] === 'string' ? nodes[0] : 'Unknown');

  for (let i = 0; i < (relations?.length || 0) && i < nodes.length - 1; i++) {
    const nextNode = nodes[i + 1]?.name || (typeof nodes[i + 1] === 'string' ? nodes[i + 1] : 'Unknown');
    pathStr += ` --[${relations[i]}]--> ${nextNode}`;
  }
  return pathStr;
}

// === DEDUPLICATION & SCORING ===
const allResults = [...graphResults, ...vectorResults, ...communityResults];

const maxGraphScore = Math.max(...graphResults.map(r => r.path_score || 0), 1);
const maxVectorScore = Math.max(...vectorResults.map(r => r.score || 0), 1);

const normalizedResults = allResults.map(r => {
  let normalizedScore = 0;
  if (r.source === 'GRAPH') {
    normalizedScore = (r.path_score / maxGraphScore) * (1 / (r.path_length * 0.3 + 0.7));
  } else if (r.source === 'VECTOR') {
    normalizedScore = r.score / maxVectorScore;
  } else {
    normalizedScore = 0.5;
  }
  return { ...r, normalized_score: normalizedScore };
});

normalizedResults.sort((a, b) => b.normalized_score - a.normalized_score);

const finalResults = [];
const sourceCounts = { GRAPH: 0, VECTOR: 0, COMMUNITY: 0 };
const MAX_PER_SOURCE = 15;
const TOTAL_MAX = 30;

for (const result of normalizedResults) {
  if (finalResults.length >= TOTAL_MAX) break;
  if (sourceCounts[result.source] < MAX_PER_SOURCE) {
    finalResults.push(result);
    sourceCounts[result.source]++;
  }
}

// === OUTPUT ===
return [{
  json: {
    merged_results: finalResults,
    source_counts: sourceCounts,
    total_count: finalResults.length,
    traversal_depth_used: Math.max(...graphResults.map(r => r.path_length || 0), 0),
    hyde_document: (JSON.parse($node['WF3: HyDE & Entity Extraction'].json.choices[0].message.content || '{}').hyde_document || '')
  }
}];

// Reranker fallback handler - CORRIG√â
const rerankedResults = $json.results || [];
const mergeData = $node['Merge Graph + Vector + Community (Deep)'].json;

return [{
  json: {
    reranked_results: rerankedResults,
    merged_results: mergeData.merged_results || [],
    source_counts: mergeData.source_counts || {},
    traversal_depth: mergeData.traversal_depth_used || 0,
    fallback: rerankedResults.length === 0
  }
}];

// PATCH 3.4: Token Budgeting & Map-Reduce for Large Contexts - CORRIG√â
const MAX_TOKENS = 6000;
const CHARS_PER_TOKEN = 4;

const mergedResults = $json.merged_results || [];
const rerankedResults = $json.reranked_results || [];

let budget = MAX_TOKENS;
const allocated = { 
  reranked: [], 
  graph: [], 
  vector: [], 
  community: [] 
};

// Allocate reranked first (highest priority)
for (const item of rerankedResults.slice(0, 5)) {
  const content = typeof item === 'string' ? item : (item.document || item.content || '');
  const tokens = Math.ceil(content.length / CHARS_PER_TOKEN);
  if (budget >= tokens) {
    allocated.reranked.push(item);
    budget -= tokens;
  }
}

// Allocate remaining results by source
for (const result of mergedResults) {
  const tokens = Math.ceil((result.content?.length || 0) / CHARS_PER_TOKEN);
  if (budget < tokens) continue;

  if (result.source === 'GRAPH' && allocated.graph.length < 10) {
    allocated.graph.push(result);
    budget -= tokens;
  } else if (result.source === 'VECTOR' && allocated.vector.length < 10) {
    allocated.vector.push(result);
    budget -= tokens;
  } else if (result.source === 'COMMUNITY' && allocated.community.length < 5) {
    allocated.community.push(result);
    budget -= tokens;
  }
}

// Build compact relationship string from graph results
const relationships = allocated.graph
  .filter(g => g.path_relations && g.path_nodes)
  .map(g => {
    const nodes = g.path_nodes || [];
    const rels = g.path_relations || [];
    if (nodes.length < 2) return null;
    return `${nodes[0]?.name || 'Unknown'}-[${rels[0] || '?'}]->${nodes[1]?.name || 'Unknown'}`;
  })
  .filter(r => r !== null)
  .slice(0, 20)
  .join('; ');

return [{
  json: {
    budgeted_context: {
      reranked: allocated.reranked,
      graph: allocated.graph,
      vector: allocated.vector,
      community: allocated.community,
      relationships: relationships
    },
    tokens_used: MAX_TOKENS - budget,
    tokens_remaining: budget,
    fallback: $json.fallback || false,
    traversal_depth: $json.traversal_depth || 0
  }
}];

// Response Formatter - builds LLM Answer Synthesis request body
// Handles missing vector results when embedding credits are exhausted

// Safely get reranked vector results
let vectorResults = [];
try {
  vectorResults = $node['WF3: Cohere Reranker']?.json?.results || [];
} catch(e) {
  vectorResults = [];
}

// Safely get Neo4j data
let neo4jData = null;
try {
  neo4jData = $node['Validate Neo4j Results']?.json;
} catch(e) {}

// Safely get community data
let communityData = null;
try {
  communityData = $node['Community Summaries Fetch']?.json;
} catch(e) {}

// Safely get entity extraction data
let entityData = null;
try {
  entityData = $node['WF3: HyDE & Entity Extraction']?.json;
} catch(e) {}

// Build context from all available sources
const ctx = $json.budgeted_context || {};
const query = $node['OTEL Init'].json.query;
const traceId = $node['OTEL Init'].json.trace_id || '';
const contextParts = [];

// Graph relationships from budgeted context
if (ctx.relationships) {
  contextParts.push('Graph Relationships: ' + ctx.relationships);
}
if (ctx.graph && ctx.graph.length > 0) {
  const graphText = ctx.graph.slice(0, 5).map(g => g.content || g.text || '').filter(Boolean).join('\n');
  if (graphText) contextParts.push('Graph Context:\n' + graphText);
}

// Vector/reranked docs
if (ctx.reranked && ctx.reranked.length > 0) {
  const rerankedText = ctx.reranked.slice(0, 5).map((d, i) => {
    const content = typeof d === 'string' ? d : (d.document || d.content || d.text || '');
    return '[Doc ' + (i+1) + '] ' + content;
  }).filter(Boolean).join('\n');
  if (rerankedText) contextParts.push('Relevant Documents:\n' + rerankedText);
}

// Community summaries
if (ctx.community && ctx.community.length > 0) {
  const commText = ctx.community.slice(0, 3).map(c => c.content || c.text || '').filter(Boolean).join('\n');
  if (commText) contextParts.push('Community Summaries:\n' + commText);
}

// Fallback: if no budgeted context, try raw Neo4j data
if (contextParts.length === 0) {
  if (neo4jData && !neo4jData.skip_graph) {
    // Extract paths from Neo4j v2 format
    const values = neo4jData.data?.values || neo4jData.results?.[0]?.data?.map(d => d.row) || [];
    if (values.length > 0) {
      const pathStr = values.slice(0, 10).map(row => {
        if (Array.isArray(row)) {
          const startEntity = row[0];
          const pathNodes = row[1] || [];
          const pathRels = row[2] || [];
          const nodeNames = Array.isArray(pathNodes) ? pathNodes.map(n => n.name || n).join(' -> ') : String(pathNodes);
          return startEntity + ': ' + nodeNames + ' [' + (Array.isArray(pathRels) ? pathRels.join(', ') : '') + ']';
        }
        return JSON.stringify(row).substring(0, 200);
      }).join('\n');
      contextParts.push('Entity Graph Paths:\n' + pathStr);
    }
  }

  // Try community summaries raw data
  if (communityData) {
    const summaries = communityData.summaries || communityData;
    if (Array.isArray(summaries) && summaries.length > 0) {
      const commStr = summaries.slice(0, 3).map(s => s.summary || s.content || JSON.stringify(s).substring(0, 300)).join('\n');
      contextParts.push('Community Summaries:\n' + commStr);
    }
  }

  // Try Hyde document as last resort context
  if (contextParts.length === 0 && entityData) {
    const content = entityData.choices?.[0]?.message?.content || '';
    try {
      const parsed = JSON.parse(content);
      if (parsed.hyde_document) {
        contextParts.push('Background Knowledge:\n' + parsed.hyde_document);
      }
    } catch(e) {
      if (content.length > 20) {
        contextParts.push('Background Knowledge:\n' + content.substring(0, 1000));
      }
    }
  }
}

const fullContext = contextParts.join('\n\n');
const embedding_fallback = vectorResults.length === 0;
const traversal_depth = ctx.graph?.length || 0;
const context_sources = contextParts.length;

// Build request body for LLM Answer Synthesis
const requestBody = {
  model: 'meta-llama/llama-3.3-70b-instruct:free',
  messages: [
    {
      role: 'system',
      content: 'You are a precise knowledge graph assistant. Answer questions using ONLY the provided context. For factual questions (who/what/where/when), answer in 1-5 words. For yes/no questions, start with Yes or No. For numerical questions, give the number with unit. NEVER say you lack information - always provide your best answer from the context. Respond in the SAME LANGUAGE as the question.'
    },
    {
      role: 'user',
      content: 'Context:\n' + fullContext + '\n\nQuestion: ' + query + '\n\nAnswer concisely:'
    }
  ],
  temperature: 0.1,
  max_tokens: 300
};

return [{
  json: {
    requestBody: requestBody,
    trace_id: traceId,
    embedding_fallback: embedding_fallback,
    tokens_used: 0,
    traversal_depth: traversal_depth,
    context_sources: context_sources,
    debug_context_length: fullContext.length,
    debug_context_parts: contextParts.length
  }
}];

let hydeDocument = '';

try {
  const hydeResponse = JSON.parse($json.choices?.[0]?.message?.content || '{}');
  hydeDocument = hydeResponse.hyde_document || '';
} catch (e) {
  console.error('Failed to parse HyDE response:', e);
  hydeDocument = '';
}

// Fallback to original query if HyDE failed
if (!hydeDocument || hydeDocument.length < 10) {
  hydeDocument = $node['OTEL Init'].json.query;
}

return [{
  json: {
    hyde_document: hydeDocument,
    original_query: $node['OTEL Init'].json.query,
    hyde_success: hydeDocument.length > 10
  }
}];

// Validate Neo4j Results - handles both REST and query/v2 API formats
const neo4jData = $json;

let hasResults = false;
let errorMessage = null;

// Check for errors
if (neo4jData.errors && neo4jData.errors.length > 0) {
  errorMessage = neo4jData.errors[0].message;
}

// Check for results in different formats
// Format 1: REST API (results[0].data)
if (neo4jData.results?.[0]?.data?.length > 0) {
  hasResults = true;
}
// Format 2: Query v2 API (data.values)
if (neo4jData.data?.values?.length > 0) {
  hasResults = true;
}
// Format 3: body wrapper
if (neo4jData.body?.results?.[0]?.data?.length > 0) {
  hasResults = true;
}
if (neo4jData.body?.data?.values?.length > 0) {
  hasResults = true;
}

if (!hasResults) {
  return [{
    json: {
      skip_graph: true,
      error: errorMessage,
      results: { results: [{ data: [] }] }
    }
  }];
}

return [{ json: neo4jData }];

const embeddingData = $json;

// Check if we have a valid embedding
let embedding = [];

// Try different response formats
if (embeddingData.data?.[0]?.embedding) {
  embedding = embeddingData.data[0].embedding;
} else if (embeddingData.body?.data?.[0]?.embedding) {
  embedding = embeddingData.body.data[0].embedding;
} else if (Array.isArray(embeddingData.embedding)) {
  embedding = embeddingData.embedding;
}

// Validate embedding dimensions (should be 1536 for text-embedding-3-small)
const expectedDim = 1536;
if (embedding.length !== expectedDim) {
  throw new Error(`Invalid embedding dimension: got ${embedding.length}, expected ${expectedDim}`);
}

return [{
  json: {
    embedding: embedding,
    dimension: embedding.length
  }
}];

// Extract LLM answer and format final response
// Handle rate limit errors gracefully
const llmResponse = $json;
let answer = '';

if (llmResponse.error) {
  // LLM call failed - try to extract partial info from context
  const ctx = $node['Response Formatter']?.json || {};
  answer = 'Unable to process query at this time. Please try again.';
} else {
  answer = llmResponse.choices?.[0]?.message?.content || '';
}

if (!answer || answer.trim() === '') {
  answer = 'No answer generated';
}

const contextData = $node['Response Formatter']?.json || {};

return [{ json: {
  status: answer !== 'No answer generated' ? 'SUCCESS' : 'NO_ANSWER',
  trace_id: contextData.trace_id || '',
  response: answer.trim(),
  metadata: {
    source: 'graph_rag_llm_synthesis',
