}
if (neo4jData.body?.data?.values?.length > 0) {
  hasResults = true;
}

if (!hasResults) {
  return [{
    json: {
      skip_graph: true,
      error: errorMessage,
      results: { results: [{ data: [] }] }
    }
  }];
}

return [{ json: neo4jData }];

const embeddingData = $json;

// Check if we have a valid embedding
let embedding = [];

// Try different response formats
if (embeddingData.data?.[0]?.embedding) {
  embedding = embeddingData.data[0].embedding;
} else if (embeddingData.body?.data?.[0]?.embedding) {
  embedding = embeddingData.body.data[0].embedding;
} else if (Array.isArray(embeddingData.embedding)) {
  embedding = embeddingData.embedding;
}

// Validate embedding dimensions (should be 1536 for text-embedding-3-small)
const expectedDim = 1536;
if (embedding.length !== expectedDim) {
  throw new Error(`Invalid embedding dimension: got ${embedding.length}, expected ${expectedDim}`);
}

return [{
  json: {
    embedding: embedding,
    dimension: embedding.length
  }
}];

// Extract LLM answer and format final response
// Handle rate limit errors gracefully
const llmResponse = $json;
let answer = '';

if (llmResponse.error) {
  // LLM call failed - try to extract partial info from context
  const ctx = $node['Response Formatter']?.json || {};
  answer = 'Unable to process query at this time. Please try again.';
} else {
  answer = llmResponse.choices?.[0]?.message?.content || '';
}

if (!answer || answer.trim() === '') {
  answer = 'No answer generated';
}

const contextData = $node['Response Formatter']?.json || {};

return [{ json: {
  status: answer !== 'No answer generated' ? 'SUCCESS' : 'NO_ANSWER',
  trace_id: contextData.trace_id || '',
  response: answer.trim(),
  metadata: {
    source: 'graph_rag_llm_synthesis',
    tokens_used: contextData.tokens_used || 0,
    traversal_depth: contextData.traversal_depth || 0,
    context_sources: contextData.context_sources || 0
  }
} }];

// GitHub Error Logger for graph
const errorData = $input.first().json;
const timestamp = new Date().toISOString();
const questionId = errorData.question_id || errorData.trace_id || 'q-' + Date.now();
const errorType = errorData.error_type || errorData.error_code || 'UNKNOWN';
const pipeline = 'graph';

const logEntry = {
  timestamp: timestamp,
  pipeline: pipeline,
  question_id: questionId,
  error_type: errorType,
  error_message: (errorData.error || errorData.message || 'No message').slice(0, 500),
  input: {
    query: (errorData.query || errorData.original_query || '').slice(0, 300),
    tenant_id: errorData.tenant_id || 'benchmark'
  },
  partial_response: errorData.partial_response || errorData.response || null,
  n8n_context: {
    workflow_id: $workflow.id,
    workflow_name: $workflow.name,
    execution_id: $execution.id
  },
  performance: {
    latency_ms: errorData.latency_ms || 0,
    http_status: errorData.http_status || null
  }
};

return [{ json: logEntry }];

// Entity Extraction Logger
const input = $input.first().json;
const entities = input.entities || input.parameters?.entity_names || [];
const skipNeo4j = input.skip_neo4j || false;

const log = {
  timestamp: new Date().toISOString(),
  pipeline: 'graph',
  step: 'entity_extraction',
  query: (input.query_text || input.query || '').slice(0, 300),
  entities_extracted: entities,
  entity_count: entities.length,
  skip_neo4j: skipNeo4j,
  hyde_preview: (input.hyde_document || '').slice(0, 200)
};

// Pass through original data + log
return [{ json: { ...input, _entity_log: log } }];
